package fundamentos_02;

public class exec03_while {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		        int c = 1; 
		        while (c <= 100) {
		            if (c % 2 != 0) {
		                System.out.println(c); 
		            }
		            c++; 
		        }
		    }


	}
